mapper.select:
select mem_id, mem_pw
from mem
where mem_id = #{mem_id}
and mem_pw = #{mem_pw}

select mem_id
from mem
where
mem_name = #{mem_name}
and mem_phone = #{mem_phone}
and mem_bd =
#{mem_bd}

select mem_pw
from mem
where
mem_id = #{mem_id}
and mem_name = #{mem_name}
and mem_phone =
#{mem_phone}

mapper.update:
update mem
set mem_pw = #{new_pw}
where mem_id = #{mem_id}
